  //       console.log("Dados OK");
  //       console.log(resposta);
  //     },
  //     error:function(resposta){
  //       console.log(resposta)

  //     }
  //   })
  // }

  render(){
    return(

      <div className="container">
        <div>
          <form onSubmit={this.buscaCep.bind(this)} method="get">
            <label>Digite o Cep</label><br/>
            <input id="cep" type="text" value={this.state.cep} onChange={this.setCep} /><br/>
            <button type="submit">Buscar cep</button>
          </form>
        </div>
      <div>

        {
          <div>
          <h1>As informações encontradas para o cep: {this.state.dados.cep}</h1>
          <p>Estado: {this.state.dados.uf}</p>
          <p>Cidade: {this.state.dados.localidade}</p>
          <p>Bairro: {this.state.dados.bairro}</p>
          <p>Rua:    {this.state.dados.logradouro} </p>
         </div>
        }
      </div>       
    </div>
    )
  }
}
export default App;
