import React, {Component} from 'react';
import './App.css';
import $ from 'jquery'

class App extends Component{

  constructor(){
    super();
    this.state = {dados : [], cep:''};
    this.setCep = this.setCep.bind(this);
    this.buscaCep = this.buscaCep.bind(this);
  }

  setCep(evento){
    this.setState({cep:evento.target.value});
    console.log(evento.target.value)
  }

  buscaCep(evento){
    evento.preventDefault();
    $.ajax({
      url:'https://viacep.com.br/ws/'+this.state.cep+'/json/',
      contentType:'application/json',
      datatype:'json',
      type:'post',
      data:'{}',
      sucess:function(resposta){
        console.log("Dados OK");
        console.log();
      },
      erro:function(resposta){
        console.log("Não Ok")
      }
    })
  }

  render(){
    return(

      <div className="container">
        <div>
        <form>
          <label for="cep">Digite o Cep</label><br/>
          <input id="cep" type="text" value={this.state.cep} onChange={this.setCep} /><br/>
          <button type="submit" onSubmit={this.buscaCep}>Buscar cep</button>
        </form>
      </div>
      <div>
        <p>Rua:</p>
        <p>Bairro:</p>
        <p>Cidade:</p>
      </div>
      </div>
    )
  }
}
export default App;
