import React, {Component} from 'react';
import './App.css';
import $ from 'jquery'

class App extends Component{

  constructor(){
    super();
    this.state = {dados : [], cep:''};
    this.setCep = this.setCep.bind(this);
    this.buscaCep = this.buscaCep.bind(this);
  }

  setCep(evento){
    this.setState({cep:evento.target.value});
  }

  buscaCep(evento){
    console.log("Evento disparado")
    evento.preventDefault();
    console.log("N達o carregou")
    $.ajax({
      url:'http://viacep.com.br/ws/'+this.state.cep+'/json/',
      contentType:'application/json',
      dataType:'htnl',
      type:'post',
      sucess:function(resposta){
        console.log("Dados OK");
        console.log(resposta);
      },
      error:function(resposta){
        console.log("N達o Ok")
      }
    })
  }

  render(){
    return(

      <div className="container">
        <div>
        <form onSubmit={this.buscaCep.bind(this)} method="get">
          <label>Digite o Cep</label><br/>
          <input id="cep" type="text" value={this.state.cep} onChange={this.setCep} /><br/>
          <button type="submit">Buscar cep</button>
        </form>
      </div>
      <div>
        <p>Rua:</p>
        <p>Bairro:</p>
        <p>Cidade:</p>
      </div>
      </div>
    )
  }
}
export default App;
